        //     .then(data => {
        //         setLog(data.log);
        //         alert(data.log);
        //         // console.log(data)
        //     });
    };


    var show = (e) => {

        if (e.target.checked) {
            e.target.parentElement.parentElement.querySelector("#usr_pass").type = "text";
        } else {
            e.target.parentElement.parentElement.querySelector("#usr_pass").type = "password";
        }
    }
    return (
        <div className="overlay">
            <center>


                <form method="POST" action="/api/user_login">
                    <div class="mb-3">
                        <label for="email" class="form-label">Email address</label>
                        <input type="email" name="email" id="usr_email" class="form-control" aria-describedby="emailHelp" />
                        <div id="emailHelp" class="form-text">We'll never share your email with anyone else.</div>
                    </div>
                    <div class="mb-3">
                        <label for="password" class="form-label">Password</label>
                        <input type="password" name="password" id="usr_pass" class="form-control" />
                    </div>
                    <button type="button" onClick={login} class="btn btn-primary">Submit</button>
                </form>
