import Card from "./card";
import useGlobalState from "../globalState";
import { useEffect } from "react";
function AddCart() {
    const [email, setEmail] = useGlobalState('email');
    useEffect(() => {
        //var url = new URL(window.location.hostname + "/api/cart_product");
        //url.searchParams.append('x', 42);
        alert(window.location.hostname + "/api/cart_product");
        fetch("/api/cart_product/?id=" + email)
            .then(response => response.json())
            .then(data => {
                alert(data);
                // console.log(data)
            });
    }, [email]);


    return (
        <div className="container-fluid add_cart">
            <div className="container">
                <Card />
                <Card />
                <Card />
                <Card />
            </div>

        </div>
    )
}

export default AddCart;
